
public class parameters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		printline(2,3);
		printline(4,5);

	}
	public static void printline(int number, int count) {
		for(int i=0; i<=count;i++) {
			System.out.print(number);
		}
		System.out.println();
	}

}
